package com.sj.board.service;

import java.util.ArrayList;

import org.springframework.stereotype.Service;

import com.sj.board.domain.PostVO;
import com.sj.board.mapper.PostMapper;

import lombok.AllArgsConstructor;

@Service
@AllArgsConstructor
public class PostServiceImplement implements PostService {
	private PostMapper mapper;
	
	/* 게시글 로드 */
	@Override
	public ArrayList<PostVO> getPostList() {
		return mapper.getPostList();
	}

	/* 게시글 등록 */
	@Override
	public int addPost(String p_title, String p_content, String m_code) {
		return mapper.addPost(p_title, p_content, m_code);
	}

	/* 게시글 조회 */
	@Override
	public PostVO getPostDetail(String queryString) {
		return mapper.getPostDetail(queryString);
	}

	@Override
	public int getRec1(String p_code) {
		return mapper.getRec1(p_code);
	}
	
	@Override
	public int plusRec1(String p_code, int p_rec1) {
		return mapper.plusRec1(p_code, p_rec1);
	}

	@Override
	public int getRec2(String p_code) {
		return mapper.getRec2(p_code);
	}

	@Override
	public int plusRec2(String p_code, int p_rec2) {
		return mapper.plusRec2(p_code, p_rec2);
	}

	@Override
	public void delPost(String p_code) {
		mapper.delPost(p_code);
	}
}
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   